insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3088,'','default','One format is included by default: <em>Plain text</em> (which removes all HTML tags). Additional formats may be created by your installation profile when you install Drupal, and more can be created by an administrator on the <a href=\"@text-formats\">Text formats page</a>.','','7.57');
