insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3215,'','default','With the Image module you can scale, crop, resize, rotate and desaturate images without affecting the original image using <a href=\"@image\">image styles</a>. When you change an image style, the module automatically refreshes all created images. Every image style must have a name, which will be used in the URL of the generated images. There are two common approaches to naming image styles (which you use will depend on how the image style is being applied):','','7.57');
