insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3219,'','default','Image module also allows you to attach images to content as fields. To add an image field to a <a href=\'@content-type\'>content type</a>, go to the content type\'s <em>manage fields</em> page, and add a new field of type <em>Image</em>. Attaching images to content this way allows image styles to be applied and maintained, and also allows you more flexibility when theming.','','7.57');
