insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3455,'','default','<p>Add a custom block to your site, named \"Welcome\" . With its text format set to \"PHP code\" (or another format supporting PHP input), add the following in the Block body:</p>\n<pre>\nprint t(\'Welcome visitor! Thank you for visiting.\');\n</pre>','','none');
insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3456,'','default','<p>To display the name of a registered user, use this instead:</p>\n<pre>\nglobal $user;\nif ($user->uid) {\n  print t(\'Welcome @name! Thank you for visiting.\', array(\'@name\' => format_username($user)));\n}\nelse {\n  print t(\'Welcome visitor! Thank you for visiting.\');\n}\n</pre>','','none');
