insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3468,'','default','The Profile module allows site administrators to define custom fields (such as country, full name, or age) for user profiles, which are then displayed in the <a href=\"@user\">My Account</a> section. This permits users of a site to share more information about themselves, and can help community-based sites organize users around specific information. For more information, see the online handbook entry for <a href=\"@profile\">Profile module</a>.','','none');
