insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3472,'','default','The Profile module enables links to see further information about site users. You can view both a main <a href=\"@profile\">User list page</a>, and more specified pages by clicking on linked fields in any profile. For example, the <a href=\"@profile-country\">People who live in Canada</a> listing on Drupal.org displays all users who have entered <em>Canada</em> in the <em>Country</em> field on their user profile.','','none');
