insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3698,'','default','The System module also handles basic configuration options for your site, including <a href=\"@date-time-settings\">Date and time settings</a>, <a href=\"@file-system\">File system settings</a>, <a href=\"@clean-url\">Clean URL support</a>, <a href=\"@site-info\">Site name and other information</a>, and a <a href=\"@maintenance-mode\">Maintenance mode</a> for taking your site temporarily offline.','','7.57');
