insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3700,'','default','Actions are individual tasks that the system can do, such as unpublishing a piece of content or banning a user. Modules, such as the <a href=\"@trigger-help\">Trigger module</a>, can fire these actions when certain system events happen; for example, when a new post is added or when a user logs in. Modules may also provide additional actions. Visit the <a href=\"@actions\">Actions page</a> to configure actions.','','7.57');
