insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3919,'','default','The Update manager module periodically checks for new versions of your site\'s software (including contributed modules and themes), and alerts administrators to available updates. In order to provide update information, anonymous usage statistics are sent to Drupal.org. If desired, you may disable the Update manager module from the <a href=\'@modules\'>Module administration page</a>. For more information, see the online handbook entry for <a href=\'@update\'>Update manager module</a>.','','7.57');
