insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3922,'','default','A report of <a href=\"@update-report\">available updates</a> will alert you when new releases are available for download. You may configure options for the frequency for checking updates (which are performed during <a href=\"@cron\">cron</a> runs) and e-mail notifications at the <a href=\"@update-settings\">Update manager settings</a>  page.','','7.57');
