insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3924,'','default','The Update manager module allows administrators to perform updates directly through the administration interface. At the top of the <a href=\"@modules_page\">modules</a> and <a href=\"@themes_page\">themes</a> pages you will see a link to update to new releases. This will direct you to the <a href=\"@update-page\">update page</a> where you see a listing of all the missing updates and confirm which ones you want to upgrade. From there, you are prompted for your FTP/SSH password, which then transfers the files into your Drupal installation, overwriting your old files. More detailed instructions can be found in the <a href=\"@update\">online handbook</a>.','','7.57');
