insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (3999,'','default','The User module allows users to register, log in, and log out. It also allows users with proper permissions to manage user roles (used to classify users) and permissions associated with those roles. For more information, see the online handbook entry for <a href=\"@user\">User module</a>.','','7.57');
