insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (4001,'','default','The User module allows users with the appropriate <a href=\"@permissions\">permissions</a> to create user accounts through the <a href=\"@people\">People administration page</a>, where they can also assign users to one or more roles, and block or delete user accounts. If allowed, users without accounts (anonymous users) can create their own accounts on the <a href=\"@register\">Create new account</a> page.','','7.57');
insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (4002,'','default','User roles and permissions','','7.57');
insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (4003,'','default','<em>Roles</em> are used to group and classify users; each user can be assigned one or more roles. By default there are two roles: <em>anonymous user</em> (users that are not logged in) and <em>authenticated user</em> (users that are registered and logged in). Depending on choices you made when you installed Drupal, the installation process may have defined more roles, and you can create additional custom roles on the <a href=\"@roles\">Roles page</a>. After creating roles, you can set permissions for each role on the <a href=\"@permissions_user\">Permissions page</a>. Granting a permission allows users who have been assigned a particular role to perform an action on the site, such as viewing a particular type of content, editing or creating content, administering settings for a particular module, or using a particular function of the site (such as search).','','7.57');
insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (4004,'','default','The <a href=\"@accounts\">Account settings page</a> allows you to manage settings for the displayed name of the anonymous user role, personal contact forms, user registration, and account cancellation. On this page you can also manage settings for account personalization (including signatures and user pictures), and adapt the text for the e-mail messages that are sent automatically during the user registration process.','','7.57');
