insert  into `drupal757_locales_source`(`lid`,`location`,`textgroup`,`source`,`context`,`version`) values (5162,'/admin/config?render=overlay','default','4 byte UTF-8 for @driver is not activated, but it is supported on your system. It is recommended that you enable this to allow 4-byte UTF-8 input such as emojis, Asian symbols and mathematical symbols to be stored correctly. See the <a href=\"@url\">documentation on adding 4 byte UTF-8 support</a> for more information.','','7.58');
