insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (2819,'Block（区块）模块让你可以创建内容的盒状容器，这样你可以将内容放置在一个或多个页面的某个区域中。核心的Seven管理主题，例如，使用了区域 \"Content\"、\"Help\"、\"Dashboard main\" 及 \"Dashboard sidebar\"，区块就可以在其中一个区域进行显示。<a href=\"@blocks\">区块管理</a>页面提供了可区块可拖曳到区域的方式，可用来控制在区域中区块的顺序。更多信息，请查看<a href=\"@block\">Block模块</a>的在线手册条目。','zh-hans',0,0);
