insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3246,'语言代码或其他自定义字串，如果<em>检测及选择</em>设置中使用了URL路径前缀，则用作URL语言检测的路径前缀。对于默认语言这个值可能留空。<strong>修改这个值会导致已有路径失效，正式网站使用时需谨慎对待。</strong>比如：指定 \"deutsch\" 作为德文网站的路径前缀时，网址会以类似 \"example.com/deutsch/contact\" 的路径格式呈现。','zh-hans',0,0);
