insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3256,'Locale（本地化）模块让你的Drupal站点可以除了默认的英文外使用其他的语言进行显示，也就是多语言化。本地化模块通过维护一个翻译的数据库，检查即将进行显示的文本进行运作。当有可用的文本翻译时则会显示翻译文本而不是源文本。如果无可用的翻译文本，则显示源文本，然后会保存以供以后翻译人员的查看。更多信息，查看<a href=\"@locale\">Locale模块</a>的在线手册条目。','zh-hans',0,0);
