insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3268,'此页面从一个Gettext Portable Object（<em>.po</em>）文件中导入翻译过的字符串。通常是以翻译包（每个翻译包都包含一些<em>.po</em>文件）的一部分进行发布，一个<em>.po</em>文件在离线编辑后就可以进行导入。导入<em>.po</em>文件可能会花费很长的时间。','zh-hans',0,0);
