insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3417,'如果站点在内容权限上发生问题，可能需要重建权限缓存。重建缓存会清除内容的所有权限并基于当前模块及设置替换权限。如果内容量巨大或权限设置很复杂，重建缓存可能会花上一段时间。在重建缓存完成后，内容会自动使用新的权限。','zh-hans',0,0);
