insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3434,'Overlay模块让你在当前页面点击管理项目链接时把站点的管理页面显示在一个JavaScript浮动层里，而不替换你浏览器窗口中的页面。使用浮动层上的关闭链接返回到你点击链接时正在查看的页面。获得更多信息，请查看<a href=\"@overlay\">Overlay模块</a>在线手册。','zh-hans',0,0);
