insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3440,'Path（路径）模块让你可以为系统内部已有的路径地址指定一个别名，或自定义URL链接地址。别名不要和URL转发混淆，后者是将更改过的或不活跃的URL转发到一个新的URL地址上。除了让URL链接的可读性更高，别名还有助于搜索引擎更有效地索引站点内容。在一个内部系统路径上可以使用多个别名。要使路径别名自动化，可以安装第三方的模块<a href=\"@pathauto\">Pathauto</a>。更多信息，请查看<a href=\"@path\">Path模块</a>的在线手册条目。','zh-hans',0,0);
