insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3453,'PHP过滤器使具有适当权限的用户可以添加PHP代码，在站点处理页面时执行。对于受信任的具有PHP经验的用户来说是非常有用及灵活的工具，但是对于那些恶意的或没有经验的用户来说将会引起非常重大和危险的安全隐患。即使是有经验的用户也可能不小心输错PHP代码而危及站点。对于那些最受信任的用户，才给予PHP过滤器的使用权限，所有通过PHP过滤器添加的PHP代码都应当在使用之前进行仔细的检验。可以在Drupal.org找到一些<a href=\"@php-snippets\">PHP片段示例</a>。','zh-hans',0,0);
