insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3458,'Poll（投票）模块可以用来创建简单的调查或有相关累计结果的问题。投票是从站点用户及会员那里收集站点反馈的一种非常有效的方式。更多信息，请查看<a href=\"@poll\">Poll模块</a>的在线手册条目。','zh-hans',0,0);
