insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3468,'Profile（用户文档）模块让站点管理员可以为用户文档指定自定义的字段（比如城市，全名或者年龄等），这些字段也都会显示在<a href=\"@user\">我的帐户</a>单元上。这使得站点用户可以分享更多的个人信息，帮助社区模式的站点通过特定的信息组织用户。更多信息，查看<a href=\"@profile\">Profile模块</a>的在线手册条目。','zh-hans',0,0);
