insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3472,'用户文档模块提供了很多查看站点用户信息的链接，你可以在主要的<a href=\"@profile\">用户列表</a>页面，或者在文档的特定页面上点击字段链接进行查看。比如，Drupal.org的<a href=\"@profile-country\">加拿大用户</a>列表会显示那些在用户文档<em>城市</em>字段中输入<em>加拿大</em>的所有用户。','zh-hans',0,0);
