insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3483,'具有<em>使用搜索</em>权限的用户可以使用搜索区块及<a href=\"@search\">搜索</a>页面。具有<em>查看已发布内容</em>权限的用户可以搜索包含特定关键字的内容。具有<em>查看用户文档</em>权限的用户可以包含某关键字的用户名，具有<em>管理用户</em>权限的用户还可以通过邮件地址搜索用户。另外，具有<em>使用高级搜索</em>权限的用户可以在<a href=\"@search\">搜索</a>页面上点击<em>高级搜索</em>选项使用更复杂的搜索方式来过滤其搜索结果。','zh-hans',0,0);
