insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3489,'默认情况下，搜索模块只支持匹配特定关键字的内容搜索。你可以安装特定语言的语言词干模块（比如用于美语的<a href=\"http://drupal.org/project/porterstemmer\">Porter Stemmer</a>模块），可以让你在搜索引擎中匹配如walk、walking和walked之类的词。另外一种方式是使用第三方的带有词干及部分关键词配配技术的搜索引擎，比如<a href=\"http://drupal.org/project/apachesolr\">Apache Solr</a>或<a href=\"http://drupal.org/project/sphinx\">Sphinx</a>。可以访问Drupal.org下载这些及其他的<a href=\"@contrib-search\">搜索相关模块</a>。','zh-hans',0,0);
