insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3547,'Testing（测试）模块提供了自动化的单元测试的框架。可以用来验证代码更改之后Drupal站点的工作状况，或用于站点开发，编写及执行模块的测试。更多信息，请查看<a href=\"@simpletest\">Testing模块</a>的在线手册条目。','zh-hans',0,0);
