insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3550,'在测试运行后，会在每个测试组旁边显示测试通过、失败还是中断的消息。通过就意味着返回了所期望的结果，当然失败就是没有了。中断通常是指测试时外部发生了错误，比如一个PHP警告或通知。如果没有失败或终端，测试结果就会显示详细信息，测试失败或终端则会以红色或粉色的行进行指示。然后你就可以使用这些结果调整你的代码和测试，直到测试通过为止。','zh-hans',0,0);
