insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (3689,'System（系统）模块就是站点的整体，提供了基本但可以通过其他的模块及主题进行功能的扩展。Drupal的一些内在元素是包含于系统模块之中的，并由其进行管理，包括了如缓存、启用禁用模块及主题、准备和显示管理化页面、以及站点基础配置等。很多关键的系统维护操作也是系统模块的一部分。更多信息，请查看<a href=\"@system\">System模块</a>的在线手册条目。','zh-hans',0,0);
