insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4108,'Field UI（字段界面）模块提供了管理化的用户界面，用于附加及管理字段。字段可以为内容项目及评论以内容类型级别进行指定，为分类术语以词汇表级别机型指定，以及为用户以站点级别进行指定。其他的模块也可能会为其数据指定字段。字段类型（文本、图像、数字等等）是由模块进行指定，由<a href=\"@field\">Field模块</a>进行收集和管理。更多信息，请查看<a href=\"@field_ui\" target=\"_blank\">Field UI模块</a>的在线手册条目。','zh-hans',0,0);
insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4109,'规划字段','zh-hans',0,0);
