insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4111,'字段有一个<em>标签</em>（用户界面中显示的名称）以及一个<em>机器名</em>（内部使用的名称）。在字段创建之后标签如有需要是可以进行修改的，但是机器名在字段创建之后是不能进行更改的。','zh-hans',0,0);
insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4112,'存的是何种字段数据','zh-hans',0,0);
insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4113,'每个字段都会存储一种数据（文本、数字、文件等等）。当你指定一个数据的时候，你就会选择某一特定的<em>字段类型</em>，用来存储相应类型的数据。在字段创建之后，其数据类型是不能进行修改的。','zh-hans',0,0);
