insert  into `drupal757_locales_target`(`lid`,`translation`,`language`,`plid`,`plural`) values (4128,'评论上的字段是内容类型级别的，显示在内容类型编辑页面（访问<a href=\"@types\">内容类型</a>页面）上的<em>评论字段</em>标签上。当你为评论添加一个字段时，那么每个内容项目的评论上都会将那个字段添加进去。比如，你想在论坛的评论中添加一个网站字段，在评论时显示一个链接到评论发布者的网站的链接。','zh-hans',0,0);
